const __pageData = JSON.parse('{"title":"Access Google Drive Reliably in Ubuntu","description":"Alternative method for accessing google drive in debian desktops.","frontmatter":{"title":"Access Google Drive Reliably in Ubuntu","description":"Alternative method for accessing google drive in debian desktops.","summary":null,"keywords":"Debian, Ubuntu, Linux, Bash, PC","image":"https://i.imgur.com/qC6ZiJM.png"},"headers":[{"level":2,"title":"Save complex command to single keyword","slug":"save-complex-command-to-single-keyword","link":"#save-complex-command-to-single-keyword","children":[{"level":3,"title":"End","slug":"end","link":"#end","children":[]}]}],"relativePath":"blog/linux/Convert Bash Scripts Into Named CLI Commands in Linux.md"}');
