const __pageData = JSON.parse('{"title":"Access Google Drive Reliably in Ubuntu","description":"Alternative method for accessing google drive in debian desktops.","frontmatter":{"title":"Access Google Drive Reliably in Ubuntu","description":"Alternative method for accessing google drive in debian desktops.","summary":"Do you think ubuntu online accounts is taking so much to just load your drive folder. Try this alternative.","keywords":"Debian, Ubuntu, Linux, Bash, PC, GoogleDrive","image":"https://i.imgur.com/qC6ZiJM.png"},"headers":[{"level":2,"title":"The Context","slug":"the-context","link":"#the-context","children":[]},{"level":2,"title":"The Problem","slug":"the-problem","link":"#the-problem","children":[]},{"level":2,"title":"The Solution","slug":"the-solution","link":"#the-solution","children":[]},{"level":2,"title":"End","slug":"end","link":"#end","children":[]}],"relativePath":"blog/linux/Ubuntu Accessing Google Drive from Nautilus.md"}');
